function initCopyRight() {
	var copyrightYear = new Date().getFullYear();
	var copyright = 'Copyright © '+ copyrightYear + ' MATHEMA Software GmbH'

	var text = document.getElementById("copyright").firstChild;
	text.deleteData(0, text.length);
	text.insertData(0, copyright);
}
